//SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

// source: https://docs.alchemy.com/docs/how-to-interact-with-erc-20-tokens-in-solidity

interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

contract MyUSDT {
    IERC20 public usdt;
    constructor(address _usdt) {
        usdt = IERC20(_usdt);
    }
    // Function to get the USDT balance of this contract
    function getUSDTBalance(address account) external view returns (uint256) {
        return usdt.balanceOf(account);
    }
    // Function to transfer USDT tokens
    function transferUSDT(address recipient, uint256 amount) external returns (bool) {
        require(usdt.transfer(recipient, amount), "Transfer failed");
        return true;
    }
    // Function to approve USDT tokens for spending
    function approveUSDT(address _spender, uint256 _amount) external {
        require(usdt.approve(_spender, _amount), "Approval failed");
    }
    // Function to transfer USDT tokens from one address to another
    function transferUSDTFrom(address _from, address _to, uint256 _amount) external {
        require(usdt.transferFrom(_from, _to, _amount), "TransferFrom failed");
    }
    // Function to get the allowance for a spender
    function getAllowance(address _owner, address _spender) external view returns (uint256) {
        return usdt.allowance(_owner, _spender);
    }
    // Function to get the total supply of USDT tokens
    function getTotalSupply() external view returns (uint256) {
        return usdt.totalSupply();
    }
}
